\begin{align*}
{ \rm{y}} = {{ \rm{A}}_{ \rm{2}}} + ( {{ \rm{A}}_{ \rm{1}}} - {{ \rm{A}}_{ \rm{2}}} ) / ( {{ \rm{1}} + {{ ( {x / {x_0}} )}^{nH}}} ),
\end{align*}
