}{}\begin{equation*}s{c_{{\rm{RefDOM}}}} = -10\,{\log _{10}}FD{R_{{\rm{HET}}}},\,{\rm{if}}\,{n_{\rm R}} \ge {n_{\rm V}},\,\,{\rm otherwise}\,0;\end{equation*}\end{document}
