\begin{document}
}{}\begin{equation*}s{c_{{\rm{HET}}}} = -10\,{\rm{lo}}{{\rm{g}}_{10}}\left( {{\rm{max}}\left( {FD{R_{{\rm{RefHOM}}}},FD{R_{{\rm{VarHOM}}}}} \right)/FD{R_{{\rm{HET}}}}} \right);\end{equation*}\end{document}
